"use client"

import Image from "next/image"
import Link from "next/link"

import { type HeroBlockProps } from "@/lib/blocks/hero.types"
import { cn } from "@/lib/utils"
import { Button } from "@/registry/new-york-v4/ui/button"

import { HeroButton } from "../../ui/hero-button"

// Hero 17 - Masonry Grid (Variable height Pinterest-style - Travel theme)
const hero17Defaults = {
  badge: "Trusted Travelers",
  title: "Journey\nBeyond",
  description:
    "Exclusive destinations and curated experiences from around the globe.",
  primaryCta: { label: "Explore", href: "#" },
  secondaryCta: {
    label: "Our Story",
    href: "#",
    variant: "outline" as const,
  },
  images: [
    {
      src: "/elements/landscape/sea/2.webp",
      alt: "Travel destination 1",
    },
    {
      src: "/elements/landscape/plane/3.webp",
      alt: "Airplane travel",
    },
    {
      src: "/elements/landscape/sea/3.webp",
      alt: "Travel destination 2",
    },
  ],
}

export function Hero17({ content = {}, classNames = {} }: HeroBlockProps) {
  const {
    badge = hero17Defaults.badge,
    title = hero17Defaults.title,
    description = hero17Defaults.description,
    primaryCta = hero17Defaults.primaryCta,
    secondaryCta = hero17Defaults.secondaryCta,
    images = hero17Defaults.images,
  } = content as typeof hero17Defaults

  return (
    <section
      className={cn(
        "relative flex min-h-[calc(100svh-5rem)] flex-col overflow-hidden",
        classNames.root
      )}
    >
      <div className="container flex-1 px-4 py-6">
        <div className="grid h-full min-h-[calc(100svh-8rem)] gap-4 lg:grid-cols-4">
          {/* Column 1 - Tall Image */}
          <div className="bg-muted relative min-h-[20rem] overflow-hidden rounded-3xl lg:row-span-2 lg:min-h-full">
            <Image
              src={images[0].src}
              alt={images[0].alt}
              fill
              className="object-cover"
              priority
            />
          </div>

          {/* Column 2 - Text Content */}
          <div className="flex flex-col gap-4 lg:col-span-2 lg:row-span-2">
            {/* Title Card */}
            <div className="bg-muted flex flex-1 flex-col justify-center rounded-3xl p-8 lg:p-10">
              {badge && (
                <p
                  className={cn(
                    "text-brand mb-4 text-sm font-semibold tracking-widest uppercase",
                    classNames.header?.badge
                  )}
                >
                  {badge}
                </p>
              )}
              <h1
                className={cn(
                  "display text-4xl leading-[1.1] tracking-tight sm:text-5xl md:text-6xl lg:text-7xl",
                  classNames.header?.title
                )}
              >
                {title.split("\n").map((line, i) => (
                  <span key={i}>
                    {line}
                    {i < title.split("\n").length - 1 && <br />}
                  </span>
                ))}
              </h1>

              {description && (
                <p
                  className={cn(
                    "text-muted-foreground mt-6 text-lg md:text-xl",
                    classNames.header?.description
                  )}
                >
                  {description}
                </p>
              )}

              <div className="mt-8 flex flex-wrap items-center gap-3">
                {primaryCta && (
                  <Link
                    href={primaryCta.href}
                    className={classNames.cta?.primary}
                  >
                    <HeroButton>{primaryCta.label}</HeroButton>
                  </Link>
                )}
                {secondaryCta && (
                  <Button
                    asChild
                    variant="outline"
                    size="lg"
                    className={cn("text-foreground", classNames.cta?.secondary)}
                  >
                    <Link href={secondaryCta.href}>{secondaryCta.label}</Link>
                  </Button>
                )}
              </div>
            </div>

            {/* Wide Image - Below text */}
            <div className="bg-muted relative min-h-[12rem] overflow-hidden rounded-3xl">
              <Image
                src={images[1].src}
                alt={images[1].alt}
                fill
                className="object-cover"
              />
            </div>
          </div>

          {/* Column 4 - Tall Image */}
          <div className="bg-muted relative min-h-[20rem] overflow-hidden rounded-3xl lg:row-span-2 lg:min-h-full">
            <Image
              src={images[2].src}
              alt={images[2].alt}
              fill
              className="object-cover"
            />
          </div>
        </div>
      </div>
    </section>
  )
}
