import * as React from "react"
import { ArrowRight } from "lucide-react"

import { cn } from "@/lib/utils"
import { Button } from "@/registry/new-york-v4/ui/button"

function HeroButton({
  className,
  children,
  ...props
}: React.ComponentProps<typeof Button>) {
  return (
    <Button
      size="lg"
      className={cn("relative rounded-full pr-12", className)}
      {...props}
    >
      {children}
      <div className="bg-background absolute inset-y-0 right-1 my-auto flex aspect-square size-8 items-center justify-center rounded-full">
        <ArrowRight className="text-foreground size-4" />
      </div>
    </Button>
  )
}

export { HeroButton }
