"use client"

import Link from "next/link"

import { ThemeToggle } from "@/registry/new-york-v4/ui/theme-toggle"

// Inline logo icon
function LogoIcon({ className }: { className?: string }) {
  return (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 24 24"
      fill="currentColor"
      className={className}
    >
      <path d="M12 1 21.5 6.5V17.5L13 22.4211V11.4234L3.49793 5.92225 12 1ZM2.5 7.6555V17.5L11 22.4211V12.5765L2.5 7.6555Z" />
    </svg>
  )
}

const footerLinks = {
  documentation: [
    { label: "Introduction", href: "/docs" },
    { label: "Installation", href: "/docs/installation" },
    { label: "CLI", href: "/docs/cli" },
    { label: "Theming", href: "/docs/theming" },
  ],
  pages: [
    { label: "Components", href: "/docs/components" },
    { label: "Blocks", href: "/blocks" },
    { label: "Templates", href: "/templates" },
    { label: "Changelog", href: "/changelog" },
  ],
  connect: [
    { label: "GitHub", href: "https://github.com/pitsi-ui/pitsi", external: true },
    { label: "Twitter", href: "https://twitter.com/pitsiui", external: true },
    { label: "Discord", href: "https://discord.gg/pitsiui", external: true },
  ],
  legal: [
    { label: "Privacy", href: "/privacy" },
    { label: "Terms", href: "/terms" },
  ],
}

export function LandingFooter1() {
  return (
    <footer className="bg-transparent pt-12 pb-6">
      <div className="container px-6">
        {/* Links Grid */}
        <div className="mb-12">
          <h5 className="border-border mb-6 border-b pb-3 text-sm font-semibold">
            General
          </h5>
          <div className="grid grid-cols-2 gap-6 text-[13px] leading-relaxed tracking-[-0.15px] md:grid-cols-4">
            {/* Documentation */}
            <div className="flex flex-col items-start">
              <h6 className="mb-2 font-medium">Documentation</h6>
              {footerLinks.documentation.map((link) => (
                <div key={link.href} className="py-px">
                  <Link
                    href={link.href}
                    className="opacity-60 transition-opacity hover:opacity-100"
                  >
                    {link.label}
                  </Link>
                </div>
              ))}
            </div>

            {/* Pages */}
            <div className="flex flex-col items-start">
              <h6 className="mb-2 font-medium">Pages</h6>
              {footerLinks.pages.map((link) => (
                <div key={link.href} className="py-px">
                  <Link
                    href={link.href}
                    className="opacity-60 transition-opacity hover:opacity-100"
                  >
                    {link.label}
                  </Link>
                </div>
              ))}
            </div>

            {/* Connect */}
            <div className="flex flex-col items-start">
              <h6 className="mb-2 font-medium">Connect</h6>
              {footerLinks.connect.map((link) => (
                <div key={link.href} className="py-px">
                  {link.external ? (
                    <a
                      href={link.href}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="opacity-60 transition-opacity hover:opacity-100"
                    >
                      {link.label}
                    </a>
                  ) : (
                    <Link
                      href={link.href}
                      className="opacity-60 transition-opacity hover:opacity-100"
                    >
                      {link.label}
                    </Link>
                  )}
                </div>
              ))}
            </div>

            {/* Legal */}
            <div className="flex flex-col items-start">
              <h6 className="mb-2 font-medium">Legal</h6>
              {footerLinks.legal.map((link) => (
                <div key={link.href} className="py-px">
                  <Link
                    href={link.href}
                    className="opacity-60 transition-opacity hover:opacity-100"
                  >
                    {link.label}
                  </Link>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Bottom section with logo and copyright */}
        <div className="border-border flex items-center justify-between border-t pt-6">
          <p className="text-muted-foreground text-xs">
            Â© 2025 pitsi/ui. All rights reserved.
          </p>
          <div className="flex items-center gap-3">
            <ThemeToggle mode="light-dark-system" className="w-auto" />
            <LogoIcon className="text-foreground size-4" />
          </div>
        </div>
      </div>
    </footer>
  )
}
