"use client"

import Image from "next/image"

import { cn } from "@/lib/utils"
import { Marquee } from "@/registry/new-york-v4/ui/marquee"

interface Review {
  name: string
  username: string
  body: string
  img: string
}

const reviews: Review[] = [
  {
    name: "Alex Chen",
    username: "@alexchen_dev",
    body: "pitsi/ui cut my development time in half. The blocks are so well designed I barely need to customize them.",
    img: "/avatars/01.webp",
  },
  {
    name: "Sarah Miller",
    username: "@sarahmills",
    body: "Finally a component library that looks good out of the box. No more fighting with styles for hours.",
    img: "/avatars/02.webp",
  },
  {
    name: "Marcus Johnson",
    username: "@marcusj_",
    body: "The animation components are incredible. Added scroll effects to my landing page in minutes.",
    img: "/avatars/03.webp",
  },
  {
    name: "Emily Park",
    username: "@empark_ui",
    body: "Best investment I've made for my freelance business. Clients love the polished look.",
    img: "/avatars/04.webp",
  },
  {
    name: "David Wilson",
    username: "@dwilsondev",
    body: "Switched from another UI library and never looked back. The DX is unmatched.",
    img: "/avatars/05.webp",
  },
  {
    name: "Lisa Thompson",
    username: "@lisadev",
    body: "The MCP integration is a game changer. My AI assistant now knows exactly which components to use.",
    img: "/avatars/06.webp",
  },
]

function ReviewCard({ img, name, username, body }: Review) {
  return (
    <figure
      className={cn(
        "bg-card relative w-64 cursor-pointer overflow-hidden rounded-xl border p-4",
        "border-border hover:bg-accent transition-colors"
      )}
    >
      <div className="flex flex-row items-center gap-2">
        <div className="relative size-8 overflow-hidden rounded-full">
          <Image
            src={img}
            alt={name}
            fill
            className="object-cover"
            sizes="32px"
          />
        </div>
        <div className="flex flex-col">
          <figcaption className="text-sm font-medium">{name}</figcaption>
          <p className="text-muted-foreground text-xs">{username}</p>
        </div>
      </div>
      <blockquote className="text-muted-foreground mt-2 text-sm">
        {body}
      </blockquote>
    </figure>
  )
}

export function LandingTestimonials1() {
  const firstRow = reviews.slice(0, reviews.length / 2)
  const secondRow = reviews.slice(reviews.length / 2)

  return (
    <div className="bg-background relative -mt-10 flex w-full flex-col items-center justify-center overflow-hidden pb-10">
      <Marquee pauseOnHover className="[--duration:20s]">
        {firstRow.map((review) => (
          <ReviewCard key={review.username} {...review} />
        ))}
      </Marquee>
      <Marquee reverse pauseOnHover className="[--duration:20s]">
        {secondRow.map((review) => (
          <ReviewCard key={review.username} {...review} />
        ))}
      </Marquee>
      <div className="from-background pointer-events-none absolute inset-y-0 left-0 w-1/3 bg-gradient-to-r" />
      <div className="from-background pointer-events-none absolute inset-y-0 right-0 w-1/3 bg-gradient-to-l" />
    </div>
  )
}
