import { clsx } from "clsx"
import { forwardRef, useState } from "react"
import { TextInput, type TextInputProps, View, Text } from "react-native"
import { twMerge } from "tailwind-merge"

function cn(...inputs: (string | undefined | null | false)[]) {
  return twMerge(clsx(inputs))
}

export interface InputProps extends TextInputProps {
  className?: string
  label?: string
  error?: string
}

const Input = forwardRef<React.ComponentRef<typeof TextInput>, InputProps>(
  ({ className, label, error, onFocus, onBlur, ...props }, ref) => {
    const [isFocused, setIsFocused] = useState(false)

    return (
      <View className="w-full">
        {label && (
          <Text className="mb-1.5 text-sm font-medium text-foreground">
            {label}
          </Text>
        )}
        <TextInput
          ref={ref}
          className={cn(
            "h-12 w-full rounded-xl border bg-background px-4 text-base text-foreground",
            isFocused ? "border-primary" : "border-input",
            error && "border-destructive",
            className
          )}
          placeholderTextColor="#71717a"
          onFocus={(e) => {
            setIsFocused(true)
            onFocus?.(e)
          }}
          onBlur={(e) => {
            setIsFocused(false)
            onBlur?.(e)
          }}
          {...props}
        />
        {error && (
          <Text className="mt-1.5 text-sm text-destructive">{error}</Text>
        )}
      </View>
    )
  }
)

Input.displayName = "Input"

export { Input }
