"use client"

import {
  createContext,
  memo,
  useContext,
  useMemo,
  useRef,
  useState,
} from "react"
import Image from "next/image"
import { motion, useScroll, useTransform, type MotionValue } from "motion/react"

import { cn } from "@/lib/utils"

// Inline ParallaxImage component
const ParallaxImage = memo<{
  src: string
  alt?: string
  className?: string
  imageClassName?: string
  range?: [string, string]
  offset?: [string, string]
  children?: React.ReactNode
}>(
  ({
    src,
    alt = "",
    className,
    imageClassName,
    range = ["0%", "50%"],
    offset = ["start start", "end start"],
    children,
  }) => {
    const ref = useRef<HTMLDivElement>(null)
    const { scrollYProgress } = useScroll({
      target: ref,
      offset: offset as any,
    })
    const y = useTransform(scrollYProgress, [0, 1], range)

    return (
      <div ref={ref} className={cn("relative overflow-hidden", className)}>
        <motion.div
          style={{ y }}
          className={cn("relative h-full w-full", imageClassName)}
        >
          <Image
            src={src}
            alt={alt}
            fill
            className="object-cover"
            sizes="100vw"
            priority
          />
        </motion.div>
        {children}
      </div>
    )
  }
)
ParallaxImage.displayName = "ParallaxImage"

// Context for horizontal scroll
const ScrollProgressContext = createContext<MotionValue<number> | null>(null)

// Inline HorizontalScrollContainer component
const HorizontalScrollContainer = memo<{
  children: React.ReactNode
  className?: string
}>(({ children, className }) => {
  const ref = useRef<HTMLDivElement>(null)
  const { scrollYProgress } = useScroll({
    target: ref,
    offset: ["start end", "end start"],
  })

  return (
    <ScrollProgressContext.Provider value={scrollYProgress}>
      <div ref={ref} className={className}>
        {children}
      </div>
    </ScrollProgressContext.Provider>
  )
})
HorizontalScrollContainer.displayName = "HorizontalScrollContainer"

// Inline HorizontalSlide component
const HorizontalSlide = memo<{
  children: React.ReactNode
  direction?: "left" | "right"
  distance?: number
  className?: string
}>(({ children, direction = "left", distance = 150, className }) => {
  const scrollYProgress = useContext(ScrollProgressContext)
  const dir = direction === "left" ? -1 : 1
  const translateX = useTransform(
    scrollYProgress || new MotionValue<number>(),
    [0, 1],
    [distance * dir, -distance * dir]
  )

  if (!scrollYProgress) {
    return (
      <div className={cn("relative flex whitespace-nowrap", className)}>
        {children}
      </div>
    )
  }

  return (
    <motion.div
      style={{ x: translateX }}
      className={cn("relative flex whitespace-nowrap", className)}
    >
      {children}
    </motion.div>
  )
})
HorizontalSlide.displayName = "HorizontalSlide"

// Inline TextGradientOpacity component
const TextGradientOpacity = memo<{
  text: string
  className?: string
  offset?: [string, string]
}>(({ text, className, offset = ["start 0.9", "start 0.25"] }) => {
  const ref = useRef<HTMLDivElement>(null)
  const { scrollYProgress } = useScroll({
    target: ref,
    offset: offset as any,
  })

  const words = useMemo(() => text.split(" "), [text])

  return (
    <div ref={ref} className={cn("relative", className)}>
      <p className="flex max-w-4xl flex-wrap gap-x-2 gap-y-1 font-bold">
        {words.map((word, i) => (
          <OpacityWord
            key={i}
            word={word}
            index={i}
            totalWords={words.length}
            scrollYProgress={scrollYProgress}
          />
        ))}
      </p>
    </div>
  )
})
TextGradientOpacity.displayName = "TextGradientOpacity"

// Helper component for word opacity animation
const OpacityWord = memo<{
  word: string
  index: number
  totalWords: number
  scrollYProgress: any
}>(({ word, index, totalWords, scrollYProgress }) => {
  const start = index / totalWords
  const end = start + 1 / totalWords
  const opacity = useTransform(scrollYProgress, [start, end], [0.2, 1])

  return (
    <motion.span style={{ opacity }} className="text-foreground">
      {word}
    </motion.span>
  )
})
OpacityWord.displayName = "OpacityWord"

// Block preview component with lazy loading
function BlockPreview({
  index,
  className = "",
}: {
  index: number
  className?: string
}) {
  const [isInView, setIsInView] = useState(false)
  const containerRef = useRef<HTMLDivElement>(null)

  // Intersection observer for lazy loading
  useMemo(() => {
    if (typeof window === "undefined") return
    const element = containerRef.current
    if (!element) return

    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setIsInView(true)
          }
        })
      },
      { threshold: 0.1, rootMargin: "200px" }
    )

    observer.observe(element)
    return () => observer.disconnect()
  }, [])

  return (
    <div
      ref={containerRef}
      className={cn(
        "bg-muted relative aspect-video w-full overflow-hidden rounded-2xl border shadow-sm",
        className
      )}
    >
      <div className="flex h-full items-center justify-center">
        <div className="text-muted-foreground text-sm">Preview {index}</div>
      </div>
    </div>
  )
}

// Inline StripeBgGuides component
function StripeBgGuides({
  contained = false,
  glowOpacity = 0.5,
}: {
  contained?: boolean
  glowOpacity?: number
}) {
  return (
    <div
      className={cn(
        "pointer-events-none absolute inset-0 z-0",
        contained && "container mx-auto"
      )}
    >
      {/* Vertical lines */}
      <div className="absolute inset-0 flex justify-between px-6">
        <div className="bg-border/30 w-px" />
        <div className="bg-border/30 hidden w-px md:block" />
        <div className="bg-border/30 hidden w-px md:block" />
        <div className="bg-border/30 w-px" />
      </div>
      {/* Glow effect */}
      <div
        className="from-brand/10 absolute inset-x-0 top-0 h-96 bg-gradient-to-b to-transparent"
        style={{ opacity: glowOpacity }}
      />
    </div>
  )
}

export function Showcase1() {
  return (
    <div
      id="design"
      className="bg-background relative isolate flex flex-col overflow-hidden"
    >
      <StripeBgGuides contained glowOpacity={0.8} />

      {/* Intro Section with Parallax Background */}
      <div className="relative flex min-h-[32rem] flex-col items-center justify-start gap-6 pt-16 pb-[10vh] text-center md:min-h-svh">
        <ParallaxImage
          src="/marketing/man-sky.webp"
          alt="Design showcase background"
          className="pointer-events-none absolute inset-0 z-0 h-full w-full"
          imageClassName="object-cover object-bottom"
          range={["-5%", "5%"]}
          offset={["start end", "end start"]}
        />
        <ParallaxImage
          src="/marketing/man-sky-no-bg.webp"
          alt="Design showcase foreground"
          className="pointer-events-none absolute inset-0 z-50 h-full w-full"
          imageClassName="object-cover object-bottom"
          range={["-5%", "5%"]}
          offset={["start end", "end start"]}
        />
        <div className="relative z-10 container px-6">
          <div className="grid grid-cols-6 gap-6">
            <div className="col-span-6 flex flex-col items-center gap-2 text-center md:gap-4">
              <p className="text-sm font-medium tracking-widest text-white uppercase">
                Where Engineering Meets Aesthetics
              </p>
              <h2 className="display text-[12vw] leading-[0.85] tracking-tight text-white md:text-[8vw]">
                Unparalleled
              </h2>
              <div className="flex items-center">
                <h2 className="display text-[12vw] leading-[0.85] tracking-tight text-white md:text-[8vw]">
                  Design
                </h2>
              </div>
            </div>
            <div className="col-span-6 mt-16 flex flex-col items-start md:col-span-2">
              <p className="text-base text-white">Every detail considered.</p>
              <p className="text-base text-white">
                From typography to color, spacing to motion.
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Horizontal Parallax - The Problem */}
      <HorizontalScrollContainer className="bg-background relative flex min-h-[50vh] flex-col justify-center overflow-hidden py-12 md:min-h-[80vh] md:py-24">
        <HorizontalSlide
          direction="left"
          distance={400}
          className="text-foreground/5 display text-[20vw] leading-none font-bold tracking-tighter whitespace-nowrap"
        >
          HOURS WASTED • STILL LOOKS OFF • HOURS WASTED •
        </HorizontalSlide>
        <div className="absolute inset-0 flex items-center justify-center">
          <div className="container px-6 text-center">
            <p className="text-brand mb-4 text-xs font-medium tracking-widest uppercase">
              We Get It
            </p>
            <h2 className="display text-foreground mx-auto max-w-4xl text-4xl leading-tight tracking-tight md:text-6xl lg:text-7xl">
              You&apos;re not a designer.
              <br />
              <span className="text-foreground">That&apos;s the point.</span>
            </h2>
          </div>
        </div>
        <HorizontalSlide
          direction="right"
          distance={400}
          className="text-foreground/5 display text-[20vw] leading-none font-bold tracking-tighter whitespace-nowrap"
        >
          SOMETHING&apos;S WRONG • CAN&apos;T TELL WHAT • SOMETHING&apos;S WRONG
          •
        </HorizontalSlide>
      </HorizontalScrollContainer>

      {/* Text Gradient Opacity - The Solution */}
      <div className="bg-muted relative min-h-[40vh] overflow-hidden md:min-h-[70vh]">
        <div className="relative flex min-h-[40vh] items-center py-12 md:min-h-[70vh] md:py-24">
          <div className="container px-6">
            <p className="text-brand mb-6 text-xs font-medium tracking-widest uppercase">
              Every Pixel Already Considered
            </p>
            <TextGradientOpacity
              text="Obsessive whitespace. Intentional contrast. Rhythm in every layout. Components with soul — designed by humans who sweat the details, so you don't have to."
              className="[&_p]:text-foreground [&_p]:text-3xl [&_p]:leading-tight [&_p]:font-normal md:[&_p]:text-5xl lg:[&_p]:text-6xl"
            />
          </div>
        </div>
      </div>

      {/* Final CTA */}
      <HorizontalScrollContainer className="bg-muted relative flex min-h-[50vh] flex-col justify-center overflow-hidden py-12 md:min-h-[80vh] md:py-24">
        <HorizontalSlide
          direction="left"
          distance={400}
          className="text-foreground/5 display text-[20vw] leading-none font-bold tracking-tighter whitespace-nowrap"
        >
          PIXEL PERFECT • CLEAN LAYOUTS • PRO QUALITY •
        </HorizontalSlide>
        <div className="absolute inset-0 flex items-center justify-center">
          <div className="container px-6 text-center">
            <p className="text-brand mb-4 text-xs font-medium tracking-widest uppercase">
              Level Up Your Project&apos;s Design
            </p>
            <h2 className="display text-foreground mx-auto max-w-4xl text-4xl leading-tight tracking-tight md:text-6xl lg:text-7xl">
              Ship work you&apos;re proud of.
              <br />
              <span className="text-foreground">Finally.</span>
            </h2>
          </div>
        </div>
        <HorizontalSlide
          direction="right"
          distance={400}
          className="text-foreground/5 display text-[20vw] leading-none font-bold tracking-tighter whitespace-nowrap"
        >
          DESIGN SYSTEM • VISUAL POLISH • ZERO COMPROMISES •
        </HorizontalSlide>
      </HorizontalScrollContainer>

      {/* Design Principles Grid */}
      <div className="relative z-50 overflow-hidden py-10 md:py-20">
        <div className="container px-6">
          <div className="mb-10 grid grid-cols-6 gap-6 md:mb-16">
            <div className="relative col-span-6 flex gap-3 rounded-lg border-dashed md:col-span-2 md:block md:border-l md:p-5">
              <div>
                <h3 className="font-medium capitalize md:mb-2 md:text-xl">
                  Typography
                  <span className="bg-brand absolute -left-px hidden h-6 w-px md:inline-block"></span>
                </h3>
                <p className="text-muted-foreground text-sm md:text-base">
                  Carefully crafted type scales, line heights, and letter
                  spacing that make your content effortlessly readable.
                </p>
              </div>
            </div>
            <div className="relative col-span-6 flex gap-3 rounded-lg border-dashed md:col-span-2 md:block md:border-l md:p-5">
              <div>
                <h3 className="font-medium capitalize md:mb-2 md:text-xl">
                  Spacing & Rhythm
                  <span className="bg-brand absolute -left-px hidden h-6 w-px md:inline-block"></span>
                </h3>
                <p className="text-muted-foreground text-sm md:text-base">
                  Consistent spacing tokens and vertical rhythm throughout.
                  Everything aligns, nothing feels off.
                </p>
              </div>
            </div>
            <div className="relative col-span-6 flex gap-3 rounded-lg border-dashed md:col-span-2 md:block md:border-l md:p-5">
              <div>
                <h3 className="font-medium capitalize md:mb-2 md:text-xl">
                  Color & Contrast
                  <span className="bg-brand absolute -left-px hidden h-6 w-px md:inline-block"></span>
                </h3>
                <p className="text-muted-foreground text-sm md:text-base">
                  Accessible color palettes with perfect contrast ratios. Light
                  and dark modes that actually look good.
                </p>
              </div>
            </div>
          </div>

          {/* Preview Grid */}
          <div className="grid grid-cols-1 gap-4 md:grid-cols-3 md:gap-6">
            {[1, 2, 3, 4, 5, 6].map((i) => (
              <BlockPreview key={i} index={i} />
            ))}
          </div>
        </div>
      </div>
    </div>
  )
}
